#include "hdl_list.hpp"
#include "kernels/program_sources.hpp" //autogenerated not visible in qtcreator

// flag indicating initialization of the library
int clsparseInitialized = 0;

//list of program sources
hdl_list* program_sources = NULL;
//list of compiled kernels
hdl_list* kernel_cache = NULL;


//TODO: make this autogenerated
// This function build the program_sources list, it needs to be extended
// manually when new kernel is introduced to the library
void createSourcesMap ()
{
    //key and sources are created during cmake procedure where program_sources are build
   hdl_insert(program_sources, scale_key, scale);
   hdl_insert(program_sources, csrmv_alpha1_key, csrmv_alpha1);
   hdl_insert(program_sources, csrmv_alpha1_beta0_key, csrmv_alpha1_beta0);
   hdl_insert(program_sources, csrmv_beta0_key, csrmv_beta0);
   hdl_insert(program_sources, csrmv_beta1_key, csrmv_beta1);
   hdl_insert(program_sources, csrmv_general_key, csrmv_general);

}
